mapper.select:
select *
from
(select rownum as rn,t1.* from (select * from reim_value order by odertime desc) t1
where name = #{name} 
and odertime >= to_date(#{startTime}, 'yyyy-mm-dd hh24:mi:ss'))tab
where
tab.rn >=#{rowNumMin}
and
tab.rn <=#{rowNumMax}

select *
from
(select rownum as rn,t1.* from (select * from reim_value order by odertime desc) t1
where name = #{name} 
and odertime <= to_date(#{endTime}, 'yyyy-mm-dd hh24:mi:ss'))tab
where
tab.rn >=#{rowNumMin}
and
tab.rn <=#{rowNumMax}

select *
from
(select rownum as rn,t1.* from (select * from reim_value order by odertime desc) t1
where status = 1 and name = #{name}
and odertime between to_date(#{startTime}, 'yyyy-mm-dd hh24:mi:ss') and to_date(#{endTime},'yyyy-mm-dd hh24:mi:ss'))tab
where
tab.rn >=#{rowNumMin}
and
tab.rn <=#{rowNumMax}

